typeProperties.script:
section Section1;
shared Parquet1 = let AdfDoc = AzureStorage.DataLakeContents("https://oreillyadfetl.dfs.core.windows.net/output/datalake/HandyCandy.parquet"), Parquet = Parquet.Document(AdfDoc) in Parquet;
shared UserQuery = let Source = #"Parquet1",
  #"Removed columns" = Table.RemoveColumns(Source, {"TransactionId", "StoreId"}),
  #"Added custom" = Table.AddColumn(#"Removed columns", "SalesMonth", each Date.AddDays([TransactionDate],-Date.Day([TransactionDate])+1)),
  #"Removed columns 1" = Table.RemoveColumns(#"Added custom", {"TransactionDate"}),
  #"Added custom 1" = Table.AddColumn(#"Removed columns 1", "ItemCount", each 1),
  #"Grouped rows" = Table.Group(#"Added custom 1", {"Product", "SalesMonth"}, {{"UnitsSold", each List.Sum([ItemCount]), type any}, {"SalesValueUSD", each List.Sum([Price]), type nullable number}}),
  #"Added custom 2" = Table.AddColumn(#"Grouped rows", "Retailer", each "Handy Candy") in #"Added custom 2";
